import { triggerEdgeCollapse } from "@/packages/tailwindcss-jun-layout";
import { PanelLeftClose, PanelRightClose } from "lucide-react";

export default function EdgeSidebarHoverExpandDemo() {
  return (
    <div className="w-full border-4 rounded bg-background">
      <div className="jun-layout jun-layout-h-[300px] jun-layout-standalone">
        <header className="jun-header">Header</header>
        <aside
          id="edgeSidebar-hoverExpand-demo"
          className="jun-edgeSidebar jun-edgeSidebar-collapsed-w-[80px] jun-edgeSidebar-permanent-hoverExpand"
        >
          <div className="jun-edgeContent bg-sidebar">
            <button
              className="jun-edgeCollapseTrigger p-4 hover:bg-neutral-200"
              onClick={(event) =>
                triggerEdgeCollapse({
                  event,
                  sidebarId: "edgeSidebar-hoverExpand-demo", // this is for demo purpose only because the demo page has more than one sidebars.
                })
              }
            >
              <PanelRightClose className="jun-edgeCollapsed-visible" />
              <PanelLeftClose className="jun-edgeUncollapsed-visible" />
            </button>
          </div>
        </aside>
        <main className="jun-content">Content</main>
      </div>
    </div>
  );
}
